        <hr />
          <label>Add Task</label>
          <input name='taskName'></input>
            <button>Add</button>
        </form>
        </div>
        <hr />

        <div className="family-tasks">
         <h1>Family Home Chore Tracker</h1>
          {tasks.map(task => (
            <div>
              <div>{task.taskName} X </div>
           </div>
